// Este flujo genera ideas de contenido para 10 días
// sobre trading/finanzas al estilo educativo de Pilo.

const topics = [
  "Introducción al trading de forex para principiantes",
  "Qué es un lote, un pip y un apalancamiento (con ejemplos sencillos)",
  "Cómo funciona el stop loss y por qué te salva la cuenta",
  "Diferencia entre análisis técnico y fundamental",
  "Indicador MACD aplicado a EURUSD",
  "Gestión de riesgo: por qué nunca arriesgar más del 1-2%",
  "Errores comunes de los traders novatos",
  "Cómo leer un gráfico de velas en 1 minuto",
  "Qué es un broker y qué debes mirar antes de elegir uno",
  "Sesiones del mercado (Londres, NY, Asia) y su impacto"
];

const today = new Date();
const data = [];

for (let i = 0; i < topics.length; i++) {
  const date = new Date(today);
  date.setDate(today.getDate() + i);

  data.push({
    day_number: i + 1,
    date: date.toISOString().split('T')[0],
    content_topic: topics[i],
    content_type: "Reel educativo",
    platform: "Instagram / TikTok",
    status: "Idea",
  });
}

// La nueva versión del nodo obliga a retornar un OBJETO:
return {
  items: data.map(entry => ({ json: entry }))
};
